import React, { useState } from 'react';
import { BsThreeDots } from 'react-icons/bs';
import { AiOutlinePlus } from 'react-icons/ai';
import './Column.css';
import { Card } from '../Card/Card';

export const Column = ({ title, tickets, users, icon }) => {
  const [showDropdown, setShowDropdown] = useState(false);
  const [showAddTicket, setShowAddTicket] = useState(false);

  const handleAddTicket = () => {
    setShowAddTicket(true);
    // You can implement the actual add ticket functionality here
  };

  const handleHideAll = () => {
    // Implement hide all functionality
    console.log('Hide all tickets in', title);
    setShowDropdown(false);
  };

  const handleClearSection = () => {
    // Implement clear section functionality
    console.log('Clear section', title);
    setShowDropdown(false);
  };

  return (
    <div className="column">
      <div className="column-header">
        <div className="header-left">
          {icon && <span className="column-icon">{icon}</span>}
          <h3>{title}</h3>
          <span className="ticket-count">{tickets.length}</span>
        </div>
        
        <div className="header-actions">
          <button 
            className="icon-button"
            onClick={handleAddTicket}
            title="Add new ticket"
          >
            <AiOutlinePlus />
          </button>
          
          <div className="dropdown-container">
            <button 
              className="icon-button"
              onClick={() => setShowDropdown(!showDropdown)}
              title="More options"
            >
              <BsThreeDots />
            </button>
            
            {showDropdown && (
              <div className="column-dropdown">
                <button onClick={handleHideAll}>
                  Hide All Tickets
                </button>
                <button onClick={handleClearSection}>
                  Clear This Section
                </button>
              </div>
            )}
          </div>
        </div>
      </div>
      
      {showAddTicket && (
        <div className="add-ticket-form">
          <input 
            type="text" 
            placeholder="Enter ticket title..."
            className="ticket-input"
          />
          <div className="form-actions">
            <button 
              className="btn-add"
              onClick={() => setShowAddTicket(false)}
            >
              Add
            </button>
            <button 
              className="btn-cancel"
              onClick={() => setShowAddTicket(false)}
            >
              Cancel
            </button>
          </div>
        </div>
      )}
      
      <div className="column-content">
        {tickets.map(ticket => (
          <Card 
            key={ticket.id} 
            ticket={ticket} 
            user={users.find(user => user.id === ticket.userId)}
          />
        ))}
      </div>
    </div>
  );
};
